import styled from '@emotion/styled';

export const Contact = styled.li`
  font-size: 20px;
  display: flex;
  justify-content: flex-start;
  gap: 30px;
  align-items: center;
  margin-bottom: 8px;
`;

export const ButtonDel = styled.button`
  width: 60px;
  border: 2px solid grey;
  border-radius: 2px;
  background-color: #44b8ad;
`;
